import React, { Component } from 'react';
import LineItem from "./LineItem";

export default class ShoppingCart extends Component {
    constructor(){
        super();
        this.state ={
            appleQuantity : 0,
            orangesQuantity : 0,
            total : 0
        }
    }

    addApples = () => {
        console.log("Inside ShoppingCart.addApple");
        this.setState({
            applesQuantity : this.state.appleQuantity + 1
        })
    }

    minusApples = () => {
        console.log("Inside ShoppingCart.minusApple");
        this.setState({
            applesQuantity : this.state.appleQuantity - 1
        })
    }


    render() {
        return (
            <div>
                <LineItem name = "Apples" cost = "$0.99" quantity = {this.state.quantity}/>
                <button id="plusApples" onClick={this.addApples}>+</button>
                <button id="minusApples" onClick={this.minusApples}>-</button>
                <input type="checkbox" id="gift" name="gift" value="giftWrap"/>
                <LineItem name = "Oranges" cost = "$1.99" quantity = {this.state.quantity}/>


            </div>
        )
    }
}
